import axios from 'axios';

export default {
  getSports: () => {
    // const key = process.env.REACT_APP_SPORTSBOOK_API_KEY;
    // console.log(key);
    return axios.get(`https://api.the-odds-api.com/v3/sports?apiKey=0c08ce7dba6fb97de149d400556558de`);
  },

  getLines: (sport, lineType) => {
    switch(lineType) {
      case 'moneyline':
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=0c08ce7dba6fb97de149d400556558de&sport=${ sport }&region=us&mkt=h2h&oddsFormat=american`);
      case 'spread':
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=0c08ce7dba6fb97de149d400556558de&sport=${ sport }&region=us&mkt=spreads&oddsFormat=american`);
      case 'totals':
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=0c08ce7dba6fb97de149d400556558de&sport=${ sport }&region=us&mkt=totals&oddsFormat=american`);
      default:
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=0c08ce7dba6fb97de149d400556558de&sport=${ sport }&region=us&mkt=h2h&oddsFormat=american`);
      }
  } 
};
