        <div className='slip-win-money'>
          <label>TO WIN</label>
          <br />
          <br />
          {/* <Input
            className='slip-to-win'
            type='text'
            value={toWin}
          /> */}
          <div className='slip-to-win' value={toWin}>{toWin}</div> 
        </div>
      </div> 

      <div className='slip-buttons'>
