import React, { useEffect, useState } from 'react';
import '../styles/BetSlip.css';
import API from '../utils/API';

const BetSlip = (e) => {
  // const betInformation = useContext(RenderLinesContext);
  const inital = false;
  const [toLose, setToLose] = useState(10);
  const [toWin, setToWin] = useState('');
  const [slipData, setSlipData] = useState({data: {}})

  // if (!e) {
  //   const [toWin, setToWin] = useState('');
  // } else {
  //   const [toWin, setToWin] = useState(toLose * e.data.slipData.odds);
  // }
  // const [slip, setSlip] = useState({});
  console.log(e);
  useEffect(() => {
    // if (initial) {
    //   setToWin(toLose * e.data.slipData.odds)
    //   inital = true;
    // }
  }, [toLose]);

  const handleChange = (e, data) => {
    console.log(data);
    console.log(e);
    setToLose(e);
    setToWin(e * data.odds * .01);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    e.persist();
    console.log(e);
    console.log(data);
    const slipData = async () => {
      await API.submitBetSlip(data)
        .then((response) => {
          console.log(response);
        })
        .catch(err => {
          console.log(err);
        })


    }
    slipData();
    console.log('test');
    // console.log(postBet);
    // console.log(data);
  };

  // console.log(e)
  // key={e.data.data.key}
  return (
    <div className='slip'>
      <div className='slip-title'>BET SLIP</div>
      {e.data.isLoading ? '' : 
        <div className='slip-body'>
          <div className='slip-exit'>X</div>
          <div className='slip-away-team'>{e.data.data.awayTeam}</div>
          <div className='slip-home-team'>vs. {e.data.data.homeTeam}</div>
          <div className='slip-bet-info'>{e.data.slipData.team} ({e.data.slipData.odds})</div>
          <div className='slip-money'>
            <div className='slip-risk-money'>
              <label>TO RISK</label>
              <br />
              <br />
              <input type='text' className='slip-to-lose' value={toLose} placeholder={toLose} onChange={(event) => handleChange(event.target.value, e.data.slipData)} /> 
            </div>
            <div className='slip-win-money'>
              <label>TO WIN</label>
              <br />
              <br />
              <div className='slip-to-win'>{toWin}</div>
            </div>
          </div>
          <div className='slip-buttons'>
            <button className='slip-button'>CANCEL</button>
            <button onClick={(event) => handleSubmit(event)} className='slip-button' id='submit-slip'>PLACE BET</button>
          </div>
      </div>
      }      
    </div>
  );
};

export default BetSlip;
