import axios from 'axios';


export default {
  getSports: () => {
    // const key = process.env.REACT_APP_SPORTSBOOK_API_KEY;
    return axios.get(`https://api.the-odds-api.com/v3/sports?apiKey=5976a83c85fcf8f9a045265e9eac9c75`);
  },

  getLines: (sport, lineType) => {
    switch(lineType) {
      case 'moneyline':
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=5976a83c85fcf8f9a045265e9eac9c75&sport=${ sport }&region=us&mkt=h2h&oddsFormat=american`);
      case 'spread':
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=5976a83c85fcf8f9a045265e9eac9c75&sport=${ sport }&region=us&mkt=spreads&oddsFormat=american`);
      case 'totals':
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=5976a83c85fcf8f9a045265e9eac9c75&sport=${ sport }&region=us&mkt=totals&oddsFormat=american`);
      default:
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=5976a83c85fcf8f9a045265e9eac9c75&sport=${ sport }&region=us&mkt=h2h&oddsFormat=american`);
      }
  },

  submitBetSlip: async (data = {}) => {
    console.log(data);
    const res = await axios('/api/bet', {
      method: 'POST',
      body: JSON.stringify(data),
      headers: { "Content-Type": "application/json" }
    });

    const json = await res.json();
    return json;

