import React, { useEffect, useState } from 'react';
import '../styles/BetSlip.css';
import API from '../utils/API';
import BetSlip from './BetSlip';

const RenderBetSlips = (props) => {
  // const [toLose, setToLose] = useState(undefined);
  // const [toWin, setToWin] = useState();
  let newSlips = props.data;
  const [slipList, setSlipList] = useState([]);

  const handleDelete = (e) => {
    console.log(e);
    console.log(e.target.id)
    console.log(slipList)
    let newList = slipList.filter((slip, id) => id != e.target.id);
    console.log(newList)
    setSlipList(newList);
    newSlips = {};
  }

  const updateSlip = (id, attributes) => {
    let index = slipList.findIndex(x => x.id == id);
    console.log(index);
    if (index == -1) {

    } else {
      // setSlipList([...])
    }
  }

  const handleChange = (e, data) => {
    console.log(data);
    console.log(e);
    console.log(data.slipData.odds.toString()[0]);
    switch (data.slipData.odds.toString()[0]) {
      case '-':
        console.log(data.slipData.odds)
        let tempCalc = Math.abs(e.target.value * data.slipData.odds * .01);
        console.log(tempCalc)
        let finalCalc = tempCalc - e.target.value;
        setToLose(e.target.value)
        console.log((e - finalCalc).toFixed(2))
        setToWin((e.target.value - finalCalc).toFixed(2));
        let index = slipList.findIndex(x => x.id == id);
        let slice = slipList.slice(0, index);
        console.log('slice', slice)
        break;
      default:
        setToLose(e);
        console.log((e * data.slipData.odds * .01).toFixed(2))
        setToWin((e * data.slipData.odds * .01).toFixed(2));
        break;
    };
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    event.persist();
    console.log(slipList);
    const userData = JSON.parse(localStorage.getItem('user'));
    const userId = userData._id;
    const gameKey = props.data.data.key;
    const betType = props.data.slipData.type;
    const team = props.data.slipData.team;
    const odds = props.data.slipData.odds;
    const outcome = null;
    const payout = null;
    const toLose = null;
    const toWin = null;
    let line = null;
    const status = 'LIVE';

    // function to post slip data based on slip type
    const slipData = async () => {
      switch (betType) {
        case 'MONEYLINE':
          await API.submitBetSlip({userId, gameKey, betType, team, line, odds, toLose, toWin, status, outcome, payout})
            .then((res) => {
              console.log(res);
            })
            .catch(err => {
              console.log(err);
            });
          break;
        case 'SPREAD':
          line = props.data.slipData.line;
          await API.submitBetSlip({userId, gameKey, betType, team, line, odds, toLose, toWin, status, outcome, payout})
            .then((res) => {
              console.log(res);
            })
            .catch(err => {
              console.log(err);
            });
          break;
        case 'TOTALS-OVER':
          line = props.data.slipData.line;
          await API.submitBetSlip({userId, gameKey, betType, team, line, odds, toLose, toWin, status, outcome, payout})
            .then((res) => {
              console.log(res);
            })
            .catch(err => {
              console.log(err);
            });
          break;
        case 'TOTALS-UNDER':
          line = props.data.slipData.line;
          await API.submitBetSlip({userId, gameKey, betType, team, line, odds, toLose, toWin, status, outcome, payout})
            .then((res) => {
              console.log(res);
            })
            .catch(err => {
              console.log(err);
            });
          break;
        default:
          console.log('none selected');
          break;
      }
    };

    slipData();
  };

  useEffect(() => {
    // setSlips(slips);
    // const slips = props.data;
    // const [slipList, setSlipList] = useState();
    console.log('mounted')
    console.log(newSlips)
    if (newSlips === undefined || newSlips === {} || newSlips === null) {

    } else {
      setSlipList(slipList => [...slipList, newSlips])
      console.log("added")

    }
    console.log(slipList)
    // setSlips(slips)

  }, [newSlips]);

  return (
    <div className='slip'>
      <div className='slip-title'>BET SLIP</div>
      {(slipList === undefined || slipList.length === 0) ? '' :
        slipList.map((slip, i) => {
          return (
            <div>
              <BetSlip data={slip} id={i} onRemove={handleDelete} onSubmit={handleSubmit} onUpdate={updateSlip}/>
            </div>
          )
        })
      }
    </div>
  )
};

export default RenderBetSlips;
