    <div>
      <table className='table'>
        <thead>
          <tr>{header.map((h, i) => <th key={i}>{h}</th>)}</tr>
        </thead>
          {sportsLines.map(game => {
            return (
              <tbody className='render-main-body'>
                <tr key={game.key}>
                  <td>
                    <table>
                      <tbody>
                        <tr>
                          <td>{game.awayTeam}</td>
                          <td>
                            <Button 
                              onClick={(e) => handleClick(e, game)}
                              value={[{
                                away: game.awayTeam,
                                line: game.awayMoneyLine
                              }]}
                              data={game}
                              className='render-moneyline'
                            >
                              {game.awayMoneyLine.toString().charAt(0)==='-' ? game.awayMoneyLine : `+${ game.awayMoneyLine }`}
                            </Button>
                          </td>
                          <td>
                            <Button 
                              onClick={(e) => handleClick(e)}
                              value={[
                                game.awayTeam,
                                game.awaySpread
                              ]}
                              data={{data: game}}
                              className='render-spread'
                            >
                              {game.awaySpread.toString().charAt(0)==='-' ? game.awaySpread  : `+${ game.awaySpread } (${ game.awaySpreadOdds })`}
                            </Button>
                          </td>
                          <td>
                            <Button 
                              onClick={(e) => handleClick(e)}
                              value={[
                                game.awayTeam,
                                game.awaySpread
                              ]}
                              data={game}
                              className='render-over'
                            >
                              O/{game.overUnder}
                            </Button>
                          </td>
                        </tr>
                      </tbody>
                    </table>
                  </td>
                </tr>
                <tr>
                  <td>
                    <table>
                      <tbody>
                        <tr>
                          <td>{game.homeTeam}</td>
                          <td>
                            <Button 
                              onClick={(e) => handleClick(e, game)}
                              value={[{
                                away: game.homeTeam,
                                line: game.homeMoneyLine
                              }]}
                              data={game}
                              className='render-moneyline'
                            >
                              {game.homeMoneyLine.toString().charAt(0)==='-' ? game.homeMoneyLine : `+${ game.homeMoneyLine }`}
                            </Button>
                          </td>
                          <td>
                            <Button 
                              onClick={(e) => handleClick(e)}
                              value={[
                                game.homeTeam,
                                game.homeSpread
                              ]}
                              data={{data: game}}
                              className='render-spread'
                            >
                              {game.homeSpread.toString().charAt(0)==='-' ? game.homeSpread  : `+${ game.homeSpread } (${ game.homeSpreadOdds })`}
                            </Button>
                          </td>
                          <td>
                            <Button 
                              onClick={(e) => handleClick(e)}
                              value={[
                                game.homeTeam,
                                game.homeSpread
                              ]}
                              data={game}
                              className='render-over'
                            >
                              O/{game.overUnder}
                            </Button>
                          </td>
                        </tr>
                      </tbody>
                    </table>
                  </td>
                </tr>
              </tbody>
            );
          })}
      </table>
      <div className='bet-slip'>
        {clickData.isLoading ? <div>bet Slip</div> : 
          <BetSlip data={clickData} />
        }
      </div>
    </div>
  );

