import axios from 'axios';
import headers from './auth';


export default {
  getSports: () => {
    // const key = process.env.REACT_APP_SPORTSBOOK_API_KEY;
    return axios.get(`https://api.the-odds-api.com/v3/sports?apiKey=c5e2d28e6740946d4477ea4d7107dbe6`);
  },

  getLines: (sport, lineType) => {
    switch(lineType) {
      case 'moneyline':
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=c5e2d28e6740946d4477ea4d7107dbe6&sport=${ sport }&region=us&mkt=h2h&oddsFormat=american`);
      case 'spread':
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=c5e2d28e6740946d4477ea4d7107dbe6&sport=${ sport }&region=us&mkt=spreads&oddsFormat=american`);
      case 'totals':
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=c5e2d28e6740946d4477ea4d7107dbe6&sport=${ sport }&region=us&mkt=totals&oddsFormat=american`);
      default:
        return axios.get(`https://api.the-odds-api.com/v3/odds?&apiKey=c5e2d28e6740946d4477ea4d7107dbe6&sport=${ sport }&region=us&mkt=h2h&oddsFormat=american`);
      }
  },

  submitBetSlip: (data = {}) => {
    console.log(data);
    const params = {
      betInfo: data
    }
    return axios.post('/api/bet', {
       params
    });
  },

  getBets: () => {
    return axios.get('/api/bet');
  },

  signup: (userData) => {
    console.log(userData);
    return axios.post('/signup', userData);
  },

  login: (email, password) => {
    return axios.post('/login', {
      email,
      password
    })
      .then((response) => {
        console.log(response.data);
        console.log(response.data.accessToken);
        if (response.data.accessToken) {
          localStorage.setItem('user', JSON.stringify(response.data));
          console.log('logged in wheres user');
        }
        return response;
      })
      .catch(err => {
        console.log(err);
      });
  }
};
