        <Button
          onClick={(event) => props.onSubmit(event)}
          className='slip-button'
          id='submit-slip'
        >
          PLACE BET
        </Button>
        {/* <button onClick={(event) => handleSubmit(event)} className='slip-button' id='submit-slip'>PLACE BET</button> */}
      </div>
    </div>
  );
};

export default BetSlip;
