import React, { useContext, useEffect, useState, useCallback } from 'react';
import { ActiveLinesContext } from './ActiveLines';
import '../styles/RenderLines.css'
import BetSlip from './BetSlip';
import Button from './Button';
export const RenderLinesContext = React.createContext();

const RenderLines = () => {
  // const [sportsLines, renderSportsLines] = useState
  const [click, setClick] = useState(false);
  const [clickData, setClickData] = useState({ data: null, isLoading: true })
  const sportsLines = useContext(ActiveLinesContext);
  console.log('hello');
  console.log(clickData);

  const handleClick = (e, game) => {
    e.preventDefault();
    e.persist();
    console.log(e);
    console.log(e.target.value)
    setClickData({ data: game,  isLoading: false })
    console.log(clickData);
  };

  useEffect(() => {
    // window.addEventListener("click", handleClick)
    if(clickData.isLoading) {

    } else {
      console.log('hey')
    }
    // console.log(sportsLines);
  // }, [sportsLines]);
    // console.log(clickData.isLoading)
    // return () => window.removeEventListener("click", handleClick);
  }, [clickData]);

  return (
    <div className='container'>
      <div className='game-container'>
        <h2>NFL</h2>
        {sportsLines.map(game => 
          <div className='render-games' key={game.key}>
            <div className='render-gamedate'>{game.gameDate}</div>
            <div className='render-gametime'>{game.gameTimeEst}</div>
            <div className='render-game'>
              <div className='render-team'>{game.awayTeam}</div>
              <Button 
                onClick={(e) => handleClick(e, game)}
                value={[{
                  away: game.awayTeam,
                  line: game.awayMoneyLine
                }]}
                  // ...game
                  // game.awayTeam,
                  // game.awayMoneyLine
                // }
                data={game}
                className='render-moneyline'
              >
              {game.awayMoneyLine.toString().charAt(0)==='-' ? game.awayMoneyLine : `+${ game.awayMoneyLine }`}
              </Button>
              <Button 
                onClick={(e) => handleClick(e)}
                value={[
                  game.awayTeam,
                  game.awaySpread
                ]}
                data={{data: game}}
                className='render-spread'
              >
              {game.awayMoneyLine.toString().charAt(0)==='-' ? game.awayMoneyLine : `+${ game.awayMoneyLine }`}
              </Button>
              <Button 
                onClick={(e) => handleClick(e)}
                value={[
                  game.awayTeam,
                  game.awaySpread
                ]}
                data={game}
                className='render-over'
              >
              O/{game.overUnder}
              </Button>


              {/* </Button> */}
              {/* <button onClick={(evt) => handleClick(evt, game)} value={{ spread: game.awaySpread, away: game.awayTeam, home: game.homeTeam }} className='render-moneyline' id='away-moneyline'>{game.awayMoneyLine.toString().charAt(0)==='-' ? game.awayMoneyLine : `+${ game.awayMoneyLine }`}</button> */}
              {/* <button className='render-spread'>{game.awaySpread.toString().charAt(0)==='-' ? game.awaySpread  : `+${ game.awaySpread } (${ game.awaySpreadOdds })`}</button>
              <button className='render-over'>O/{game.overUnder}</button> */}
            </div>
            <div className='render-game'>
              <div className='render-team'>{game.homeTeam}</div>
              <button className='render-moneyline'>{game.homeMoneyLine.toString().charAt(0)==='-' ? game.homeMoneyLine : `+${ game.homeMoneyLine }`}</button>
              <button className='render-spread'>{game.homeSpread.toString().charAt(0)==='-' ? game.homeSpread : `+${ game.homeSpread } (${ game.homeSpreadOdds })`}</button>
              <button className='render-under'>U/{game.overUnder}</button>
            </div>
          </div>
        )}
      </div>
      <div className='bet-slip'>
        {clickData.isLoading ? <div>bet Slip</div> : 
          <BetSlip data={clickData} />
        }
      </div>
    </div>

  );
};

export default RenderLines;
