import React, { useState, useEffect } from 'react';
// import { BrowserRouter as Router, Link, Redirect } from 'react-router-dom';
import { Redirect } from 'react-router-dom';
import API from '../utils/API';
import '../styles/Login.css';

const Login = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [isError, setIsError] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (event) => {
    event.preventDefault();
    API.login(email, password)
      .then((response) => {
        console.log(response);
        if (response.data.success === true) {
          console.log('logged in loginjs')
          setIsLoggedIn(true);
          // return <Redirect to='/members' />;
        } else {
          setIsError(true);
        };
      })
      .catch(() => {
        setIsError(true);
      });
  };

  useEffect(() => {
    console.log(isLoggedIn);
    if (isLoggedIn) {
      <Redirect to='/members' />;
    }
  }, [isLoggedIn]);
  // if (isLoggedIn) {
  //   API.isLoggedIn()
  //   return <Redirect to='/members' />;
  // };

  return (
    <form className='login-form'>
      <div className='login-row'>
        <input type='text' className='login-field' id='login-username' placeholder='Email' value={email} onChange={(event) => {setEmail(event.target.value)} } />
      </div>
      <div className='login-row'>
        <input type='text' className='login-field' id='login-password' placeholder='Password' value={password} onChange={(event) => {setPassword(event.target.value)} } />
      </div>
      <div className='login-row'>
        <button type='submit' className='login-button' onClick={handleSubmit}>LOGIN</button>
      </div>
    </form>
  );
};

export default Login;
