import React, {useContext, useState, useEffect} from 'react';
import BetSlipConfirm from './BetSlipConfirm';
import Button from './Button';
import RenderBetSlips from './RenderBetSlips'
import '../styles/BetSlipContainer.css';
import API from '../utils/API';
// import API from '../utils/API';

const BetSlipContainer = (props) => {
  console.log(props)
  const [slipState, setSlipState] = useState('cart')
  const [slipData, setSlipData] = useState();
  const [betStatus, setBetStatus] = useState();
  const [activeSlips, setActiveSlips] = useState([]);

  let clickedData = props.data

  if (slipData === {}) {
    clickedData=[]
    slipData=[]
  }

  console.log(slipState);
  console.log(slipData);

  const handleClick = (e) => {
    e.preventDefault();
    console.log(e)
    console.log(e.target)
    const userData = JSON.parse(localStorage.getItem('user'));
    API.getBets(userData._id)
      .then(res => {
        // setActiveSlips(res)
        console.log(res)
      })
      .catch(err => {
        console.log(err )
      })
    setSlipState('active')

  }

  useEffect(() => {
    // console.log(sports);
    // setIsLoading(false);
    // console.log(slipState)
    // console.log('update')
    // if (slipState === 'submitted') {
    //   console.log('inside here')
    //   // setSlipState('default')
    //   // setTimeout(() => {
    //   //   setSlipState('default');
    //   // }, 4000);
    // }
    // props.data=[];

    // props.updateClickData([])
    setSlipData([])
    if (slipData === null) {
      props.data=[]
      console.log('null')
    }
    // props.
    if (slipState === 'submitted') {
      console.log('correct')
      props.data = [];
    }
  }, [setSlipState]) //setSlipState

  return (
    <div className='slip-container'>
      <div className='slip-title'>BET SLIP</div>
      <div className='slip-tabs'>
        <Button
          onClick={handleClick}
          id='slip-tab-cart'
          type='button'
          className='slip-tab'
        >
        CART
        </Button>
        <Button
          onClick={handleClick}
          id='slip-tab-active'
          type='button'
          className='slip-tab'
        >
        ACTIVE
        </Button>
      </div>

      {slipState === 'cart' ?
       <RenderBetSlips passSlipState={setSlipState} passSlipData={setSlipData} data={clickedData} />
      :
      ''
      }

      {
        slipState === 'submitted' ?
          <BetSlipConfirm data={slipData} passSlipState={setSlipState} passSlipData={setSlipData} />
        : ''
      }
    </div>
  );
};

export default BetSlipContainer;
