  }

  return (
    <div className='container'>
      <div className='game-container'>
        <h2>NFL</h2>
        {sportsLines.map(game => 
          <div className='render-games' key={game.key}>
            <div className='render-gamedate'>{game.gameDate}</div>
            <div className='render-gametime'>{game.gameTimeEst}</div>
            <div className='render-game'>
              <div className='render-team'>{game.awayTeam}</div>
              <Button 
                onClick={(e) => handleClick(e)}
                value={[
                  game.awayTeam,
                  game.awaySpread
                ]}
              >
              {game.awayMoneyLine.toString().charAt(0)==='-' ? game.awayMoneyLine : `+${ game.awayMoneyLine }`}
              </Button>
              {/* </Button> */}
              {/* <button onClick={(evt) => handleClick(evt, game)} value={{ spread: game.awaySpread, away: game.awayTeam, home: game.homeTeam }} className='render-moneyline' id='away-moneyline'>{game.awayMoneyLine.toString().charAt(0)==='-' ? game.awayMoneyLine : `+${ game.awayMoneyLine }`}</button> */}
              {/* <button className='render-spread'>{game.awaySpread.toString().charAt(0)==='-' ? game.awaySpread  : `+${ game.awaySpread } (${ game.awaySpreadOdds })`}</button>
              <button className='render-over'>O/{game.overUnder}</button> */}
            </div>
            <div className='render-game'>
              <div className='render-team'>{game.homeTeam}</div>
              <button className='render-moneyline'>{game.homeMoneyLine.toString().charAt(0)==='-' ? game.homeMoneyLine : `+${ game.homeMoneyLine }`}</button>
              <button className='render-spread'>{game.homeSpread.toString().charAt(0)==='-' ? game.homeSpread : `+${ game.homeSpread } (${ game.homeSpreadOdds })`}</button>
              <button className='render-under'>U/{game.overUnder}</button>
            </div>
          </div>
        )}
      </div>
      <div className='bet-slip'><BetSlip data={clickData}/></div>
    </div>

  );
};

export default RenderLines;
