      ''
      }
      {props.data.slipData.type === 'TOTALS-OVER' ?
        <div className='slip-bet-info'><b>TOTAL:</b> {props.data.slipData.team} O/{props.data.slipData.line} ({props.data.slipData.odds})</div> :
