import React, { useEffect, useState, useContext } from 'react';
import API from '../utils/API';
import ActiveLines from './ActiveLines';
import { SportsContext } from './ActiveSports';
export const GamesContext = React.createContext();

const ActiveGames = () => {
  const [games, setGames] = useState([]);
  const sports = useContext(SportsContext);

  useEffect(() => {
    async function getMoneyLine() {
      await API.getLines(sports[1], 'moneyline')
        .then(res => {
          setGames([
            {
              moneyline: res.data.data
            }
          ]);
        })
        .catch(err => {
          console.log(err)
        });
    };

    async function getSpreadLine() {
      await API.getLines(sports[1], 'spread')
        .then(res => {
          setGames(prevGames => [
            ...prevGames, 
            {
              spread: res.data.data
            }
          ])
          console.log(games);
        })
        .catch(err => {
          console.log(err)
        });
    };

    async function getTotalsLine() {
      await API.getLines(sports[1], 'spread')
        .then(res => {
          setGames(prevGames => [
            ...prevGames, 
            {
              totals: res.data.data
            }
          ])
          console.log(games);
        })
        .catch(err => {
          console.log(err)
        });
    };


    getMoneyLine();
    getSpreadLine();
    getTotalsLine();
  }, []);

  return (
    <div>
      {/* {games.map(game => <div key={game.home_team}>{game.teams[0]} vs. {game.teams[1]}</div>)} */}
      <GamesContext.Provider value={{games}}>
        <ActiveLines />
      </GamesContext.Provider>
    </div>
  );
};

export default ActiveGames;
