import React, { useContext, useEffect } from 'react';
import { ActiveLinesContext } from './ActiveLines';
import '../styles/RenderLines.css'

const RenderLines = () => {
  // const [sportsLines, renderSportsLines] = useState
  const sportsLines = useContext(ActiveLinesContext);

  useEffect(() => {
    console.log(sportsLines);
  }, [sportsLines]);



  return (
  <div>
    {sportsLines.map(line => 
      <div className='render-game' key={line.key}>
        <div className='render-team'>{line.awayTeam} ({line.awayMoneyLine.toString().charAt(0)==='-' ? line.awayMoneyLine : `+${ line.awayMoneyLine }`})</div>
        <div className='render-team'>{line.homeTeam} ({line.homeMoneyLine.toString().charAt(0)==='-' ? line.homeMoneyLine : `+${ line.homeMoneyLine }`})</div>
      </div>
    )}
  </div>
  );
};

export default RenderLines;
