import React, { useContext, useEffect } from 'react';
import { ActiveLinesContext } from './ActiveLines';

const RenderLines = () => {
  // const [sportsLines, renderSportsLines] = useState
  const sportsLines = useContext(ActiveLinesContext);

  useEffect(() => {
    console.log(sportsLines);
  }, [sportsLines]);



  return (
  <div>
    {sportsLines.map(line => 
      <div key={line.key}>
        {line.awayTeam} ({line.awayMoneyLine.toString().charAt(0)==='-' ? line.awayMoneyLine : `+${ line.awayMoneyLine }`})
        {'\n'}
        {line.homeTeam} ({line.homeMoneyLine.toString().charAt(0)==='-' ? line.homeMoneyLine : `+${ line.homeMoneyLine }`})
      </div>)}
  </div>
  );
};

export default RenderLines;
