import React, { useContext, useEffect, useState } from 'react';
import { ActiveLinesContext } from './ActiveLines';
import '../styles/RenderLines.css'
import BetSlip from './BetSlip';
export const RenderLinesContext = React.createContext();

const RenderLines = () => {
  // const [sportsLines, renderSportsLines] = useState
  const [click, setClick] = useState({});
  const sportsLines = useContext(ActiveLinesContext);

  useEffect(() => {
    // console.log(sportsLines);
  // }, [sportsLines]);
  }, [sportsLines]);

  const handleClick = (e, data) => {
    e.preventDefault();
    e.persist();
    console.log(e);
    console.log(data);
    // setClick(e);
    return (
      <div>
        <RenderLinesContext.Provider value={[e, data]}>
          <BetSlip />
        </RenderLinesContext.Provider>
      </div>
    )
  };

  return (
  <div className='game-container'>
    <h2>NFL</h2>
    {sportsLines.map(game => 
      <div className='render-games' key={game.key}>
        <div className='render-gamedate'>{game.gameDate}</div>
        <div className='render-gametime'>{game.gameTimeEst}</div>
        <div className='render-game'>
          <div className='render-team'>{game.awayTeam}</div>
          {/* <button value={game} onClick={handleClick} className='render-moneyline'>{game.awayMoneyLine.toString().charAt(0)==='-' ? game.awayMoneyLine : `+${ game.awayMoneyLine }`}</button> */}
          <button onClick={(evt) => handleClick(evt, game)} value={game.awaySpread} className='render-moneyline' id='away-moneyline'>{game.awayMoneyLine.toString().charAt(0)==='-' ? game.awayMoneyLine : `+${ game.awayMoneyLine }`}</button>
          <button className='render-spread'>{game.awaySpread.toString().charAt(0)==='-' ? game.awaySpread  : `+${ game.awaySpread } (${ game.awaySpreadOdds })`}</button>
          <button className='render-over'>O/{game.overUnder}</button>
        </div>
        <div className='render-game'>
          <div className='render-team'>{game.homeTeam}</div>
          <button className='render-moneyline'>{game.homeMoneyLine.toString().charAt(0)==='-' ? game.homeMoneyLine : `+${ game.homeMoneyLine }`}</button>
          <button className='render-spread'>{game.homeSpread.toString().charAt(0)==='-' ? game.homeSpread : `+${ game.homeSpread } (${ game.homeSpreadOdds })`}</button>
          <button className='render-under'>U/{game.overUnder}</button>
        </div>
      </div>
    )}
  </div>
  );
};

export default RenderLines;
