            .then((res) => {
              console.log(res);
            })
            .catch(err => {
              console.log(err);
            });
          break;
        case 'TOTALS-OVER':
          line = e.data.slipData.line;
          await API.submitBetSlip({userId, gameKey, betType, team, line, odds, toLose, toWin, status, outcome, payout})
            .then((res) => {
              console.log(res);
            })
            .catch(err => {
              console.log(err);
            });
          break;
        case 'TOTALS-UNDER':
          line = e.data.slipData.line;
          await API.submitBetSlip({userId, gameKey, betType, team, line, odds, toLose, toWin, status, outcome, payout})
            .then((res) => {
              console.log(res);
            })
            .catch(err => {
              console.log(err);
            });
          break;
